import 'package:path_provider/path_provider.dart';

import 'package:sqflite/sqflite.dart';
import 'dart:io';
import 'package:path/path.dart';

// Classe de manipulação do banco de dados
class DatabaseHelper {
  final String databaseName = "hafazer.db"; // nome da base de dados
  final int databaseVersion = 1; // versão da base de dados

  // parte do singleton, ou seja, criar somente uma instância da classe helper
  // para acessar a base de dados
  static final DatabaseHelper _instance =
      new DatabaseHelper.internal(); // sqflite
  factory DatabaseHelper() => _instance;

  DatabaseHelper.internal(); // remover o erro do método internal
  static Database _db;

  // inicializa o banco de dados
  initDb() async {
    Directory docDir = await getApplicationDocumentsDirectory();
    String path = join(docDir.path, databaseName);
    var db =
        await openDatabase(path, version: databaseVersion, onCreate: _onCreate);
    return db;
  }

  void _onCreate(Database db, int version) async {
    await db.execute(
        "CREATE TABLE user(id INTEGER PRIMARY KEY autoincrement, name TEXT, email TEXT, phone TEXT, password TEXT, photo TEXT)");
    await db.execute(
        "INSERT INTO user(name, email, phone, password) VALUES('Natan Riboli', 'natanriboli@gmail.com.br', '123456', 'data:image/jpeg;base64,')");

    await db.execute(
        "CREATE TABLE meta(id INTEGER PRIMARY KEY autoincrement, descricao TEXT, concluida INTEGER)");

    await db.execute(
        "INSERT INTO meta(descricao, concluida) VALUES('Coisas Hafazer', 0)");

    await db.execute(
        "CREATE TABLE passo(id INTEGER PRIMARY KEY autoincrement, codigoMeta INTEGER, descricao TEXT, concluido INTEGER)");

    await db.execute(
        "INSERT INTO passo(codigoMeta, descricao, concluido) VALUES(1, 'Conclua seu primeiro passo!', 0)");
  }

  // método singleton em si
  Future<Database> get db async {
    if (_db != null) return _db;
    _db = await initDb();
    return _db;
  }
}
